import React from 'react';
import './App.css';

class Counter extends React.Component {
  render() {
    return <div className={this.props.className}>{this.props.count}</div>
  }
}

class Button extends React.Component {
  render() {
      const { style, label } = this.props;

      return (
          <button style={style} onClick={this.props.onClick} className={this.props.className}>
              {label}
          </button>
      );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state =  {
      count: 0,
      active: false
    }
    this.addCount = this.addCount.bind(this);
    this.reduceCount = this.cre.bind(this);
    this.toggleClass = this.toggleClass.bind(this);
  }

  addCount() {
    this.setState((prevState, { count }) => ({
      count: prevState.count + 1
    }));
  }

  reduceCount() {
    this.setState((prevState, { count }) => ({
      count: prevState.count - 1
    }));
  }

  toggleClass() {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
  }

  render() {
    console.log(this.state.count);
    return (
      <React.Fragment>
        <Button className="buttonPlus" label="+" onClick={this.addCount}/>
        <Counter className={this.state.active ? 'counterNone': 'counter'}  count={this.state.count}  />
        <Button className="buttonMinus" label="-" onClick={this.reduceCount} />
        <Button className="counterShown" label="hide" onClick={this.toggleClass} />
      </React.Fragment>
    );
  }
}

export default App;
