import React from 'react';
import './App.css';

class Counter extends React.Component {
  render() {
    return <div className={this.props.className}>{this.props.count}</div>
  }
}

class Button extends React.Component {
  render() {
      const { style, label } = this.props;

      return (
          <button style={style} onClick={this.props.onClick} className={this.props.className}>
              {label}
          </button>
      );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state =  {
      count: 0
    }
    this.countPlus = this.countPlus.bind(this);
    this.countPlus = this.countPlus.bind(this)
  }

  countPlus() {
    this.setState((prevState, { count }) => ({
      count: prevState.count + 1
    }));
  }

  countMinus() {
    this.setState((prevState, { count }) => ({
      count: prevState.count - 1
    }));
  }

  render() {
    console.log(this.state.count);
    return (
      <React.Fragment>
        <Button className="buttonPlus" label="+" onClick={this.countPlus}/>
        <Counter className="counter" count={this.state.count}  />
        <Button className="buttonMinus" label="-" onClick={this.countMinus} />
        <Button className="counterShown" label="hide" />
      </React.Fragment>
    );
  }
}

export default App;
