import React from 'react';
import './App.css';

class Counter extends React.Component {
  render() {
    return <div className={this.props.className}>{this.props.count}</div>
  }
}

class Button extends React.Component {
  render() {
      const { style, label } = this.props;

      return (
          <button style={style} onClick={this.props.onClick} className={this.props.className}>
              {label}
          </button>
      );
  }
}

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state =  {
      count: 0
    }
    this.countPlus 
  }

  countPlus() {
    this.setState({
      count: this.count+1
    })
  }

  render() {
    return (
      <React.Fragment>
        <Button className="buttonPlus" label="+" />
        <Counter className="counter" count={this.state.count} onClick={this.countPlus} />
        <Button className="buttonMinus" label="-" />
        <Button className="counterShown" label="hide" />
      </React.Fragment>
    );
  }
}

export default App;
